import React, {useState, useEffect} from 'react'
import { product } from '../product';

const CartItem = (props) => {
    const {productId, quantity} = props.data;
    const [detail, setDetail] = useState([]);
    useEffect(() => {
        const findDetail = product.filter(product => product.id === productId)[0];
        setDetail(findDetail);
    }, [productId])
    console.log(detail);
  return (
    <div className='flex justify-between items-center bg-slage-600 text-white p-2 border-b-2 border-slate-700 gap-5 rounded-md'>
      <img src={detail.image} alt='' className='w-12'/>
      <h3>{detail.name}</h3>
      <p>${detail.price * quantity}</p>
      <div>
      <button className='bg-gray-200 rounded-full w-6 h-6 text-cyan-600'></button>
      <span>{quantity}</span>
      <button className='bg-gray-200 rounded-full w-6 h-6 text-cyan-600'></button>
      </div>
      
    </div>
  )
}

export default CartItem
